import React, { useState, useEffect } from 'react';
import './App.css';
import OptionsPanel from './components/OptionsPanel';
import NewTask from './components/NewTask';
import ShowTasks from './components/ShowTask';
import UpdateTask from './components/UpdateTask';
import RemoveTask from './components/RemoveTask';

function App() {
  const [current, setCurrent] = useState('main');
  const [tasks, setTasks] = useState([]);
  const [currentId, setCurrentId] = useState(0);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const storedCurrentId = JSON.parse(localStorage.getItem('currentId')) || 0;
    setTasks(storedTasks);
    setCurrentId(storedCurrentId);
  }, []);

  const showNewTask = () => setCurrent('newTask');
  const showShowTasks = () => setCurrent('showTasks');
  const showUpdateTask = () => setCurrent('updateTask');
  const showRemoveTask = () => setCurrent('removeTask');
  const showOptionsPanel = () => setCurrent('main');

  const addTask = (newTask) => {
    newTask.id = currentId;
    setCurrentId(currentId + 1);

    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);

    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    localStorage.setItem('currentId', JSON.stringify(currentId + 1));
  };

  const deleteTask = (taskId) => {
    // Filter out the task with the provided ID
    const updatedTasks = tasks.filter((task) => task.id !== parseInt(taskId));

    setTasks(updatedTasks);

    // Update tasks in localStorage
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  };


const updateTask = (taskId, fieldToUpdate, newValue) => {
  // Find the task with the provided ID
  const taskIndex = tasks.findIndex((task) => task.id === parseInt(taskId));

  if (taskIndex === -1) {
    alert('Task not found with the provided ID.');
    return;
  }
  // Create a copy of the task with the updated field
  const updatedTasks = [...tasks];
  updatedTasks[taskIndex] = {
    ...updatedTasks[taskIndex],
    [fieldToUpdate]: newValue,
  };

  setTasks(updatedTasks);

  // Update tasks in localStorage
  localStorage.setItem('tasks', JSON.stringify(updatedTasks));
};


  return (
    <div className="App">
      {current === 'main' && (
        <OptionsPanel
          onNewTask={showNewTask}
          onShowTasks={showShowTasks}
          onUpdateTask={showUpdateTask} 
          onRemoveTask={showRemoveTask}
        />
      )}
      {current === 'newTask' && (
        <NewTask onBackToOptionsPanel={showOptionsPanel} onAddTask={addTask} />
      )}
      {current === 'showTasks' && (
        <ShowTasks tasks={tasks} onBackToOptionsPanel={showOptionsPanel} />
      )}
      {current === 'updateTask' && (
        <UpdateTask
          onUpdateTask={updateTask}
          onBackToOptionsPanel={showOptionsPanel}
        />
      )}
      {current === 'removeTask' && (
        <RemoveTask
          onDeleteTask={deleteTask}
          onBackToOptionsPanel={showOptionsPanel}
        />
      )}
    </div>
  );
}

export default App;
