import React from 'react';
import OptionsPanel from './OptionsPanel';
import NewTask from './NewTask';
import ShowTask from './ShowTask';
import UpdateTask from './UpdateTask';
import RemoveTask from './RemoveTask';

const MAIN_CARD = 'main';
const NEW_TASK_CARD = 'newTask';
const SHOW_TASKS_CARD = 'showTasks';
const UPDATE_TASK_CARD = 'updateTask';
const REMOVE_TASK_CARD = 'removeTask';

const Card = ({
  currentCard,
  addTask,
  tasks,
  deleteTask,
  updateTask,
  changeCard,
}) => {

  const renderCard = () => {
    switch (currentCard) {
      case MAIN_CARD:
        return <OptionsPanel
            onNewTask={showNewTask}
            onShowTasks={showShowTasks}
            onUpdateTask={showUpdateTask} 
            onRemoveTask={showRemoveTask}/> ;
      case NEW_TASK_CARD:
        return <NewTask onAddTask={addTask} onCancel={() => changeCard(MAIN_CARD)} />;
      case SHOW_TASKS_CARD:
        return <ShowTask tasks={tasks} onCancel={() => changeCard(MAIN_CARD)} />;
      case UPDATE_TASK_CARD:
        return <UpdateTask onUpdateTask={updateTask} onCancel={() => changeCard(MAIN_CARD)} />;
      case REMOVE_TASK_CARD:
        return <RemoveTask onDeleteTask={deleteTask} onCancel={() => changeCard(MAIN_CARD)} />;
      default:
        return null; 
    }
  };

  return <div className="card-container">{renderCard()}</div>;
};

export default Card;
