import React, { useState } from 'react';
import '../TaskCard.css';

const TaskCard = ({ onAddTask, onShowTasks }) => {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className="task-card">
      <h2>Task Card</h2>
      <div className="button-container">
        <button className="action-button" onClick={() => setShowForm(true)}>
          New Task
        </button>
        <button className="action-button" onClick={() => onShowTasks()}>
          Show Tasks
        </button>
        <button className="action-button">Update Tasks</button>
        <button className="action-button">Delete Task</button>
      </div>
      {showForm && (
        <div className="form-container">
          <h3>Create New Task</h3>
          {/* Add form elements for creating a new task */}
          <button
            className="action-button"
            onClick={() => {
              setShowForm(false);
              onAddTask();
            }}
          >
            Add Task
          </button>
        </div>
      )}
    </div>
  );
};

export default TaskCard;
