import React from 'react';
import './OptionsPanel.css';

const OptionsPanel = ({ onNewTask, onShowTasks, onUpdateTask, onRemoveTask }) => {
  return (
    <div className="options-panel">
      <h2>Task Manager</h2>
      <div className="button-container">
        <button className="action-button" onClick={onNewTask}>
          New Task
        </button>
        <button className="action-button" onClick={onShowTasks}>
          Show Tasks
        </button>
        <button className="action-button" onClick={onUpdateTask}>
          Update Task
        </button>
        <button className="action-button" onClick={onRemoveTask}>
          Remove Task
        </button>
      </div>
    </div>
  );
};

export default OptionsPanel;
