import { saveAs } from 'file-saver';

  const saveTasksAsJSON = (tasks) => {
    const jsonData = JSON.stringify(tasks);
    const blob = new Blob([jsonData], { type: 'application/json' });
    saveAs(blob, 'tasks.json');
  };

  export const addTask = (newTask, tasks) => {
    const maxId = tasks.reduce((max, task) => (task.id > max ? task.id : max), -1);
    newTask.id = maxId + 1;

    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);
    saveTasksAsJSON(tasks);
  };

  const updateTask = (taskId, fieldToUpdate, newValue, tasks) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === taskId) {
        return {
          ...task,
          [fieldToUpdate]: newValue,
        };
      }
      return task;
    });

    setTasks(updatedTasks);
    saveTasksAsJSON(tasks);
  };


  export const removeTask = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(updatedTasks);
    saveTasksAsJSON(tasks);
  };
