// src/App.js
import React, { useState } from 'react';
import './App.css';
import CreateTask from './CreateTask';
import UpdateTask from './UpdateTask';

function App() {
  const [tasks, setTasks] = useState([]);

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  const updateTask = (taskId, updatedTask) => {
    const updatedTasks = tasks.map((task) =>
      task.id === taskId ? updatedTask : task
    );
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Task Manager</h1>
      </header>
      <div className="App-content">
        <TaskForm addTask={addTask} updateTask={updateTask} />
        <CreateTask tasks={tasks} addTask={addTask} />
        <UpdateTask tasks={tasks} updateTask={updateTask} />
      </div>
    </div>
  );
}

function TaskForm({ addTask, updateTask }) {
  const [option, setOption] = useState('create');

  const handleOptionChange = (e) => {
    setOption(e.target.value);
  };

  return (
    <div className="TaskForm">
      <h2>Task Options</h2>
      <label>
        <input
          type="radio"
          value="create"
          checked={option === 'create'}
          onChange={handleOptionChange}
        />
        Create Task
      </label>
      <label>
        <input
          type="radio"
          value="update"
          checked={option === 'update'}
          onChange={handleOptionChange}
        />
        Update Task
      </label>
      {option === 'create' ? (
        <CreateTaskForm addTask={addTask} />
      ) : (
        <UpdateTaskForm updateTask={updateTask} />
      )}
    </div>
  );
}

function CreateTaskForm({ addTask }) {
  const [task, setTask] = useState('');

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const handleCreateTask = () => {
    if (task.trim() === '') return;

    const newTask = { id: Date.now(), text: task };
    addTask(newTask);
    setTask('');
  };

  return (
    <div className="CreateTaskForm">
      <h2>Create Task</h2>
      <input
        type="text"
        placeholder="Enter task"
        value={task}
        onChange={handleTaskChange}
      />
      <button onClick={handleCreateTask}>Create</button>
    </div>
  );
}

function UpdateTaskForm({ updateTask }) {
  const [taskId, setTaskId] = useState('');
  const [updatedTaskText, setUpdatedTaskText] = useState('');

  const handleTaskIdChange = (e) => {
    setTaskId(e.target.value);
  };

  const handleUpdatedTaskTextChange = (e) => {
    setUpdatedTaskText(e.target.value);
  };

  const handleUpdateTask = () => {
    if (taskId.trim() === '' || updatedTaskText.trim() === '') return;

    const updatedTask = { id: parseInt(taskId), text: updatedTaskText };
    updateTask(updatedTask.id, updatedTask);
    setTaskId('');
    setUpdatedTaskText('');
  };

  return (
    <div className="UpdateTaskForm">
      <h2>Update Task</h2>
      <input
        type="text"
        placeholder="Task ID"
        value={taskId}
        onChange={handleTaskIdChange}
      />
      <input
        type="text"
        placeholder="Updated Task Text"
        value={updatedTaskText}
        onChange={handleUpdatedTaskTextChange}
      />
      <button onClick={handleUpdateTask}>Update</button>
    </div>
  );
}

export default App;
