import React, { useState } from 'react';
import '../TaskCard.css';

const TaskCard = ({ tasks, addTask }) => {
  const [showForm, setShowForm] = useState(false);
  const [taskName, setTaskName] = useState('');
  const [taskDescription, setTaskDescription] = useState('');

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const handleAddTask = () => {
    const newTask = {
      id: tasks.length + 1,
      name: taskName,
      description: taskDescription,
    };
    addTask(newTask);
    setTaskName('');
    setTaskDescription('');
  };

  return (
    <div className="task-card">
      <h2>Task Card</h2>
      {showForm ? (
        <div className="form-container">
          <input
            type="text"
            placeholder="Task Name"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Task Description"
            value={taskDescription}
            onChange={(e) => setTaskDescription(e.target.value)}
          />
          <button className="action-button" onClick={handleAddTask}>
            Add Task
          </button>
        </div>
      ) : (
        <div className="button-container">
          <button className="action-button" onClick={toggleForm}>
            New Task
          </button>
          <button className="action-button">Show Tasks</button>
          <button className="action-button">Update Tasks</button>
          <button className="action-button">Delete Task</button>
        </div>
      )}
    </div>
  );
};

export default TaskCard;
