// RemoveTaskCard.js
import React, { useState } from 'react';
import './RemoveTaskCard.css';

const RemoveTaskCard = ({ onDeleteTask, onBackToMainCard }) => {
  const [taskId, setTaskId] = useState('');

  const handleTaskIdChange = (e) => {
    setTaskId(e.target.value);
  };

  const handleRemoveTask = () => {
    onDeleteTask(taskId);
    setTaskId('');
  };

  return (
    <div className="remove-task-card">
      <h2>Remove Task</h2>
      <form>
        <div className="form-group">
          <label>Task ID:</label>
          <input
            type="text"
            name="taskId"
            value={taskId}
            onChange={handleTaskIdChange}
            required
          />
        </div>
        <button className="action-button" onClick={handleRemoveTask}>
          Remove Task
        </button>
        <button className="action-button" onClick={onBackToMainCard}>
          Back to Main Card
        </button>
      </form>
    </div>
  );
};

export default RemoveTaskCard;
