import React, { useState, useEffect } from 'react';
import './App.css';
import TaskCard from './components/TaskCard';
import TaskList from './components/TaskList';


function App() {
  const [tasks, setTasks] = useState([]);
  const [showNewTaskCard, setShowNewTaskCard] = useState(false);
  const [showTaskListCard, setShowTaskListCard] = useState(false);

  useEffect(() => {
    // Load tasks from data.json
    fetch('/data.json')
      .then((response) => response.json())
      .then((data) => setTasks(data))
      .catch((error) => console.error('Error loading tasks:', error));
  }, []);

  const addTask = (newTask) => {
    // Generate a unique ID for the new task
    newTask.id = tasks.length + 1;
    // Update the tasks state with the new task
    setTasks([...tasks, newTask]);
    setShowNewTaskCard(false); // Hide the "New Task" card after adding
  };

  return (
    <div className="App">
      {!showNewTaskCard && !showTaskListCard && (
        <TaskCard
          onAddTask={addTask}
          onShowTasks={() => setShowTaskListCard(true)}
          onShowNewTask={() => setShowNewTaskCard(true)}
        />
      )}
      {showTaskListCard && <TaskList tasks={tasks} />}
    </div>
  );
}

export default App;
