import React, { useState } from 'react';
import './App.css';
import TaskCard from './components/TaskCard';
import TaskList from './components/TaskList';
import data from './tasks.json'; // Import data.json

function App() {
  const [tasks, setTasks] = useState(data);
  const [showTaskList, setShowTaskList] = useState(false);
  const [showMainMenu, setShowMainMenu] = useState(true); // Add this state variable

  const addTask = (newTask) => {
    const newId = tasks.length > 0 ? Math.max(...tasks.map((task) => task.id)) + 1 : 1;
    newTask.id = newId;
    setTasks([...tasks, newTask]);
  };

  const handleShowTasks = () => {
    setShowTaskList(true);
    setShowMainMenu(false); // Hide the main menu
  };

  const handleBackToMainMenu = () => {
    setShowTaskList(false); // Hide the task list
    setShowMainMenu(true); // Show the main menu
  };

  return (
    <div className="App">
      {showMainMenu && ( // Conditionally render the main menu
        <TaskCard
          onShowTasks={handleShowTasks}
          onBackToMainMenu={handleBackToMainMenu} // Pass the function to go back to the main menu
        />
      )}
      {showTaskList && <TaskList tasks={tasks} />}
    </div>
  );
}

export default App;
