// src/UpdateTaskForm.js
import React, { useState } from 'react';

function UpdateTaskForm({ tasks, updateTask }) {
  const [taskId, setTaskId] = useState('');
  const [updatedTitle, setUpdatedTitle] = useState('');
  const [updatedDescription, setUpdatedDescription] = useState('');
  const [updatedDueDate, setUpdatedDueDate] = useState('');
  const [updatedStatus, setUpdatedStatus] = useState('');

  const handleTaskIdChange = (e) => {
    setTaskId(e.target.value);
  };

  const handleUpdatedTitleChange = (e) => {
    setUpdatedTitle(e.target.value);
  };

  const handleUpdatedDescriptionChange = (e) => {
    setUpdatedDescription(e.target.value);
  };

  const handleUpdatedDueDateChange = (e) => {
    setUpdatedDueDate(e.target.value);
  };

  const handleUpdatedStatusChange = (e) => {
    setUpdatedStatus(e.target.value);
  };

  const handleUpdateTask = () => {
    if (
      taskId.trim() === '' ||
      updatedTitle.trim() === '' ||
      updatedDescription.trim() === '' ||
      updatedDueDate.trim() === '' ||
      updatedStatus.trim() === ''
    )
      return;

    const updatedTask = {
      id: parseInt(taskId),
      title: updatedTitle,
      description: updatedDescription,
      dueDate: updatedDueDate,
      status: updatedStatus,
    };
    updateTask(updatedTask);
    setTaskId('');
    setUpdatedTitle('');
    setUpdatedDescription('');
    setUpdatedDueDate('');
    setUpdatedStatus('');
  };

  return (
    <div className="UpdateTaskForm">
      <h2>Update Task</h2>
      <div>
        <label>Task ID:</label>
        <input type="text" value={taskId} onChange={handleTaskIdChange} />
      </div>
      <div>
        <label>Updated Title:</label>
        <input
          type="text"
          value={updatedTitle}
          onChange={handleUpdatedTitleChange}
        />
      </div>
      <div>
        <label>Updated Description:</label>
        <textarea
          value={updatedDescription}
          onChange={handleUpdatedDescriptionChange}
        />
      </div>
      <div>
        <label>Updated Due Date:</label>
        <input
          type="date"
          value={updatedDueDate}
          onChange={handleUpdatedDueDateChange}
        />
      </div>
      <div>
        <label>Updated Status:</label>
        <input
          type="text"
          value={updatedStatus}
          onChange={handleUpdatedStatusChange}
        />
      </div>
      <button onClick={handleUpdateTask}>Update</button>
    </div>
  );
}

export default UpdateTaskForm;
