import React, { useState } from 'react';
import '../TaskCard.css';
import TaskForm from './TaskForm';

const TaskCard = ({ onShowTasks }) => {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className="task-card">
      <h2>Task Card</h2>
      <div className="button-container">
        <button className="action-button" onClick={() => setShowForm(true)}>
          New Task
        </button>
        <button className="action-button" onClick={() => onShowTasks()}>
          Show Tasks
        </button>
        <button className="action-button">Update Tasks</button>
        <button className="action-button">Delete Task</button>
      </div>
      {showForm && (
        <TaskForm
          onAddTask={() => {
            // Handle adding task here or pass it to the parent component
            setShowForm(false);
          }}
        />
      )}
    </div>
  );
};

export default TaskCard;
