import React, { useState } from 'react';
import '../TaskCard.css';

const TaskCard = () => {
  const [showForm, setShowForm] = useState(false);

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  return (
    <div className="task-card">
      <h2>Task Card</h2>
      {showForm ? (
        <div className="form-container">
          <form>
            <input type="text" placeholder="Task Name" />
            <input type="text" placeholder="Task Description" />
            <input type="text" placeholder="Due Date" />
            <input type="text" placeholder="Status" />
            <button className="action-button">Add Task</button>
          </form>
        </div>
      ) : (
        <div className="button-container">
          <button className="action-button" onClick={toggleForm}>
            New Task
          </button>
          <button className="action-button">Show Tasks</button>
          <button className="action-button">Update Tasks</button>
          <button className="action-button">Delete Task</button>
        </div>
      )}
    </div>
  );
};

export default TaskCard;
