import React, { useState } from 'react';
import '../TaskCard.css';

const TaskCard = ({ onAddTask, onShowTasks }) => {
  const [showForm, setShowForm] = useState(false);
  const [showMainMenu, setShowMainMenu] = useState(true);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
    status: 'Incomplete',
  });

  return (
    <div className="task-card">

      {showMainMenu && (
      <div className="button-container">
        <button className="action-button" onClick={() => action() }>
          New Task
        </button>
        <button className="action-button" onClick={() => onShowTasks()}>
          Show Tasks
        </button>
        <button className="action-button">Update Tasks</button>
        <button className="action-button">Delete Task</button>
      </div>
      )}
      {showForm && (
        <div className="form-container">
          <h3>Create New Task</h3>
          <form>
            <div>
              <label>Title:</label>
              <input
                type="text"
                value={newTask.title}
                onChange={(e) =>
                  setNewTask({ ...newTask, title: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                value={newTask.description}
                onChange={(e) =>
                  setNewTask({ ...newTask, description: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Due Date:</label>
              <input
                type="date"
                value={newTask.dueDate}
                onChange={(e) =>
                  setNewTask({ ...newTask, dueDate: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Status:</label>
              <select
                value={newTask.status}
                onChange={(e) =>
                  setNewTask({ ...newTask, status: e.target.value })
                }
                required
              >
                <option value="Incomplete">Incomplete</option>
                <option value="Complete">Complete</option>
              </select>
            </div>
            <button
              className="action-button"
              onClick={() => {
                onAddTask(newTask);
                setShowForm(false);
                setNewTask({
                  title: '',
                  description: '',
                  dueDate: '',
                  status: 'Incomplete',
                });
              }}
            >
              Add Task
            </button>
          </form>
        </div>
      )}
    </div>
  );

  function action(){
    setShowForm(true);
    setShowMainMenu(false)
  }
};

export default TaskCard;
