import React, { useState, useEffect } from 'react';
import './App.css';
import MainCard from './components/MainCard';
import NewTaskCard from './components/NewTaskCard';
import ShowTasksCard from './components/ShowTaskCard';
import UpdateTaskCard from './components/UpdateTaskCard';
import RemoveTaskCard from './components/RemoveTaskCard';

function App() {
  const [currentCard, setCurrentCard] = useState('main');
  const [tasks, setTasks] = useState([]);
  const [currentId, setCurrentId] = useState(0);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const storedCurrentId = JSON.parse(localStorage.getItem('currentId')) || 0;
    setTasks(storedTasks);
    setCurrentId(storedCurrentId);
  }, []);

  const showNewTaskCard = () => setCurrentCard('newTask');
  const showShowTasksCard = () => setCurrentCard('showTasks');
  const showUpdateTaskCard = () => setCurrentCard('updateTask');
  const showRemoveTaskCard = () => setCurrentCard('removeTask');
  const showMainCard = () => setCurrentCard('main');

  const addTask = (newTask) => {
    newTask.id = currentId;
    setCurrentId(currentId + 1);

    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);

    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    localStorage.setItem('currentId', JSON.stringify(currentId + 1));
  };

  const deleteTask = (taskId) => {
    // Filter out the task with the provided ID
    const updatedTasks = tasks.filter((task) => task.id !== parseInt(taskId));

    setTasks(updatedTasks);

    // Update tasks in localStorage
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  };


const updateTask = (taskId, fieldToUpdate, newValue) => {
  // Find the task with the provided ID
  const taskIndex = tasks.findIndex((task) => task.id === parseInt(taskId));

  if (taskIndex === -1) {
    alert('Task not found with the provided ID.');
    return;
  }
  // Create a copy of the task with the updated field
  const updatedTasks = [...tasks];
  updatedTasks[taskIndex] = {
    ...updatedTasks[taskIndex],
    [fieldToUpdate]: newValue,
  };

  setTasks(updatedTasks);

  // Update tasks in localStorage
  localStorage.setItem('tasks', JSON.stringify(updatedTasks));
};


  return (
    <div className="App">
      {currentCard === 'main' && (
        <MainCard
          onNewTask={showNewTaskCard}
          onShowTasks={showShowTasksCard}
          onUpdateTask={showUpdateTaskCard} 
          onRemoveTask={showRemoveTaskCard}
        />
      )}
      {currentCard === 'newTask' && (
        <NewTaskCard onBackToMainCard={showMainCard} onAddTask={addTask} />
      )}
      {currentCard === 'showTasks' && (
        <ShowTasksCard tasks={tasks} onBackToMainCard={showMainCard} />
      )}
      {currentCard === 'updateTask' && (
        <UpdateTaskCard
          onUpdateTask={updateTask}
          onBackToMainCard={showMainCard}
        />
      )}
      {currentCard === 'removeTask' && (
        <RemoveTaskCard
          onDeleteTask={deleteTask}
          onBackToMainCard={showMainCard}
        />
      )}
    </div>
  );
}

export default App;
