import React from 'react';
import './OptionsPanel.css';

const NEW_TASK_CARD = 'newTask';
const SHOW_TASKS_CARD = 'showTasks';
const UPDATE_TASK_CARD = 'updateTask';
const REMOVE_TASK_CARD = 'removeTask';

const OptionsPanel = ({ changeCard }) => {
  return (
    <div className="options-panel">
      <h2>Task Manager</h2>
      <div className="button-container">
        <button className="action-button" onClick={() => changeCard(MAIN_CARD)} >
          New Task
        </button>
        <button className="action-button" onClick={() => changeCard(MAIN_CARD)} >
          Show Tasks
        </button>
        <button className="action-button" onClick={() => changeCard(MAIN_CARD)} >
          Update Task
        </button>
        <button className="action-button" onClick={() => changeCard(MAIN_CARD)} >
          Remove Task
        </button>
      </div>
    </div>
  );
};

export default OptionsPanel;
