import React from 'react';
import NewTask from './NewTask';
import ShowTasks from './ShowTasks';
import UpdateTask from './UpdateTask';
import RemoveTask from './RemoveTask';

const Card = ({
  currentCard,
  addTask,
  tasks,
  deleteTask,
  updateTask,
  changeCard,
}) => {
  const renderCard = () => {
    switch (currentCard) {
      case 'newTask':
        return <NewTask onAddTask={addTask} onCancel={() => changeCard('main')} />;
      case 'showTasks':
        return <ShowTasks tasks={tasks} onCancel={() => changeCard('main')} />;
      case 'updateTask':
        return <UpdateTask onUpdateTask={updateTask} onCancel={() => changeCard('main')} />;
      case 'removeTask':
        return <RemoveTask onDeleteTask={deleteTask} onCancel={() => changeCard('main')} />;
      default:
        return null; 
    }
  };

  return <div className="card-container">{renderCard()}</div>;
};

export default Card;
