import React, { useState, useEffect } from 'react';

import OptionsPanel from './OptionsPanel';
import NewTask from './NewTask';
import ShowTask from './ShowTask';
import UpdateTask from './UpdateTask';
import RemoveTask from './RemoveTask';

import './Card.css';

import {
    initializeTasksAndCurrentId,
    addTask,
    deleteTask,
    updateTask,
    addTaskToJson,
    removeTaskFromJson,
    updateTaskFieldInJson,
    loadTasksFromJsonFile
  } from '../helper';

const MAIN_CARD = 'main';
const NEW_TASK_CARD = 'newTask';
const SHOW_TASKS_CARD = 'showTasks';
const UPDATE_TASK_CARD = 'updateTask';
const REMOVE_TASK_CARD = 'removeTask';

const Card = () => {

    const [currentCard, setCurrentCard] = useState('main');
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
      initializeTasksAndCurrentId(setTasks, setCurrentId);
    }, []);

    const handleAddTask = (newTask) => {
      addTaskToJson(newTask);
    };

    const handleDeleteTask = (taskId) => {
      removeTaskFromJson(taskId);
    };

    const handleUpdateTask = (taskId, fieldToUpdate, newValue) => {
      updateTaskFieldInJson(taskId, fieldToUpdate, newValue);
    };

    const changeCard = (card) => {
      setCurrentCard(card);
    };

  const renderCard = () => {
    switch (currentCard) {
      case MAIN_CARD:
        return <OptionsPanel
        changeCard={changeCard}/> ;
      case NEW_TASK_CARD:
        return <NewTask onAddTask={handleAddTask} onBackToOptionsPanel={() => changeCard(MAIN_CARD)} />;
      case SHOW_TASKS_CARD:
        return <ShowTask tasks={loadTasksFromJsonFile} onBackToOptionsPanel={() => changeCard(MAIN_CARD)} />;
      case UPDATE_TASK_CARD:
        return <UpdateTask onUpdateTask={handleUpdateTask} onBackToOptionsPanel={() => changeCard(MAIN_CARD)} />;
      case REMOVE_TASK_CARD:
        return <RemoveTask onDeleteTask={handleDeleteTask} onBackToOptionsPanel={() => changeCard(MAIN_CARD)} />;
    }
  };

  return <div className="card-container">{renderCard()}</div>;
};

export default Card;
