import React, { useState } from 'react';
import './App.css';
import TaskCard from './components/TaskCard';
import TaskList from './components/TaskList';
import data from './tasks.json'; // Import data.json

function App() {
  const [tasks, setTasks] = useState(data);
  const [showTaskList, setShowTaskList] = useState(false);

  const addTask = (newTask) => {
    const newId = tasks.length > 0 ? Math.max(...tasks.map((task) => task.id)) + 1 : 1;
    newTask.id = newId;
    setTasks([...tasks, newTask]);
  };

  return (
    <div className="App">
      {showTaskList ? (
        <TaskList tasks={tasks} onBackToMainMenu={() => setShowTaskList(false)} />
      ) : (
        <TaskCard
          onShowTasks={() => setShowTaskList(true)}
          onAddTask={addTask}
          onBackToMainMenu={() => setShowTaskList(false)} // Pass the function to go back to the main menu
        />
      )}
    </div>
  );
}

export default App;
