const JSON_FILENAME = 'tasks.json';

const loadTasksFromJsonFile = () => {
  try {
    const jsonTasks = localStorage.getItem(JSON_FILENAME);
    return jsonTasks ? JSON.parse(jsonTasks) : [];
  } catch (error) {
    console.error('Error loading tasks:', error);
    return [];
  }
};

export const getAllvTasks = () => {
  try {
    const tasks = loadTasksFromJsonFile();
    if (Array.isArray(tasks)) {
      return tasks;
    } else if (tasks && typeof tasks === 'object') {
      // In case the data is an object, attempt to convert it to an array
      const taskArray = Object.values(tasks);
      if (Array.isArray(taskArray)) {
        return taskArray;
      }
    }
    return [];
  } catch (error) {
    console.error('Error getting tasks:', error);
    return [];
  }
};

const saveTasksToJsonFile = (tasks) => {
  try {
    const jsonTasks = JSON.stringify(tasks);
    localStorage.setItem(JSON_FILENAME, jsonTasks);
  } catch (error) {
    console.error('Error saving tasks:', error);
  }
};

export const addTask = (task) => {
  const existingTasks = loadTasksFromJsonFile();
  const taskId = existingTasks.length;

  const newTask = {
    ...task,
    id: taskId,
  };

  const updatedTasks = [...existingTasks, newTask];

  saveTasksToJsonFile(updatedTasks);
};

export const removeTask = (taskId) => {
  const existingTasks = loadTasksFromJsonFile();
  const updatedTasks = existingTasks.filter((task) => task.id !== parseInt(taskId));
  saveTasksToJsonFile(updatedTasks);
};

export const updateTask = (taskId, fieldToUpdate, updatedValue) => {
  const existingTasks = loadTasksFromJsonFile();
  const updatedTasks = existingTasks.map((task) => {
    if (task.id === parseInt(taskId)) {
      return {
        ...task,
        [fieldToUpdate]: updatedValue,
      };
    }
    return task;
  });
  saveTasksToJsonFile(updatedTasks);
};
