import React, { useState } from 'react';
import './RemoveTask.css';

const RemoveTask = ({ onDeleteTask, onBackToOptionsPanel }) => {
  const [taskId, setTaskId] = useState('');

  const handleTaskIdChange = (e) => {
    setTaskId(e.target.value);
  };

  const handleRemoveTask = () => {
    onDeleteTask(taskId);
    setTaskId('');
  };

  return (
    <div className="remove-task">
      <h2>Remove Task</h2>
      <form>
        <div className="form-group">
          <label>Task ID:</label>
          <input
            type="text"
            name="taskId"
            value={taskId}
            onChange={handleTaskIdChange}
            required
          />
        </div>
        <button className="button" onClick={handleRemoveTask}>
          Remove Task
        </button>
        <button className="button" onClick={onBackToOptionsPanel}>
          Back
        </button>
      </form>
    </div>
  );
};

export default RemoveTask;
