import { saveAs } from 'file-saver';

const JSON_FILENAME = 'tasks.json';

export const loadTasksFromJsonFile = () => {
  try {
    const jsonTasks = localStorage.getItem(JSON_FILENAME);
    return jsonTasks ? JSON.parse(jsonTasks) : [];
  } catch (error) {
    console.error('Error loading tasks:', error);
    return [];
  }
};

const saveTasksToJsonFile = (tasks) => {
  try {
    const jsonTasks = JSON.stringify(tasks);
    localStorage.setItem(JSON_FILENAME, jsonTasks);
  } catch (error) {
    console.error('Error saving tasks:', error);
  }
};

export const addTaskToJson = (task) => {
  const existingTasks = loadTasksFromJsonFile();
  const taskId = existingTasks.length;

  const newTask = {
    ...task,
    id: taskId,
  };

  const updatedTasks = [...existingTasks, newTask];

  saveTasksToJsonFile(updatedTasks);
};


export const removeTaskFromJson = (taskId) => {
  const existingTasks = loadTasksFromJsonFile();
  const updatedTasks = existingTasks.filter((task) => task.id !== parseInt(taskId));
  console.log("updatedTasks: " ,updatedTasks);
  saveTasksToJsonFile(updatedTasks);
};

export const updateTaskFieldInJson = (taskId, fieldToUpdate, updatedValue) => {
  const existingTasks = loadTasksFromJsonFile();
  const updatedTasks = existingTasks.map((task) => {
    if (task.id === taskId) {
      return {
        ...task,
        [fieldToUpdate]: updatedValue,
      };
    }
    return task;
  });
  saveTasksToJsonFile(updatedTasks);
};


const saveTasksAsJSON = () => {
  const jsonData = JSON.stringify(tasks, null, 2);
  const blob = new Blob([jsonData], { type: 'application/json' });
  saveAs(blob, 'tasks.json');
};

const updateTask = (taskId, updatedTask) => {
  const updatedTasks = tasks.map((task) =>
    task.id === taskId ? { ...task, ...updatedTask } : task
  );
  const jsonData = JSON.stringify(updatedTasks, null, 2);
  const blob = new Blob([jsonData], { type: 'application/json' });
  saveAs(blob, 'tasks.json');
};

const removeTask = (taskId) => {
  const updatedTasks = tasks.filter((task) => task.id !== taskId);
  const jsonData = JSON.stringify(updatedTasks, null, 2);
  const blob = new Blob([jsonData], { type: 'application/json' });
  saveAs(blob, 'tasks.json');
};
