import React, { useState } from 'react';
import '../TaskCard.css';
import TaskForm from './TaskForm';

const TaskCard = ({ onShowTasks, onBackToMainMenu }) => {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className="task-card">
      <h2>Task Card</h2>
      <div className="button-container">
        <button className="action-button" onClick={() => setShowForm(true)}>
          New Task
        </button>
        {onShowTasks && ( // Conditionally render the "Show Tasks" button
          <button className="action-button" onClick={() => onShowTasks()}>
            Show Tasks
          </button>
        )}
        {onBackToMainMenu && ( // Conditionally render the "Back to Main Menu" button
          <button className="action-button" onClick={() => onBackToMainMenu()}>
            Back to Main Menu
          </button>
        )}
        <button className="action-button">Update Tasks</button>
        <button className="action-button">Delete Task</button>
      </div>
      {showForm && <TaskForm />}
    </div>
  );
};

export default TaskCard;
