import React, { useState, useEffect } from 'react';
import './App.css';
import OptionsPanel from './components/OptionsPanel';
import Card from './components/Card';

const CARD_MAIN = 'main';
const CARD_NEW_TASK = 'newTask';
const CARD_SHOW_TASKS = 'showTasks';
const CARD_UPDATE_TASK = 'updateTask';
const CARD_REMOVE_TASK = 'removeTask';

function App() {
  const [currentCard, setCurrentCard] = useState(CARD_MAIN);
  const [tasks, setTasks] = useState([]);
  const [currentId, setCurrentId] = useState(0);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const storedCurrentId = JSON.parse(localStorage.getItem('currentId')) || 0;
    setTasks(storedTasks);
    setCurrentId(storedCurrentId);
  }, []);

  const updateLocalStorage = (key, value) => {
    localStorage.setItem(key, JSON.stringify(value));
  };

  const changeCard = (card) => {
    setCurrentCard(card);
  };

  const addTask = (newTask) => {
    newTask.id = currentId;
    setCurrentId(currentId + 1);

    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);

    updateLocalStorage('tasks', updatedTasks);
    updateLocalStorage('currentId', currentId + 1);
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== parseInt(taskId));
    setTasks(updatedTasks);
    updateLocalStorage('tasks', updatedTasks);
  };

  const updateTask = (taskId, fieldToUpdate, newValue) => {
    const taskIndex = tasks.findIndex((task) => task.id === parseInt(taskId));

    if (taskIndex === -1) {
      console.error('Task not found with the provided ID.');
      return;
    }

    const updatedTasks = [...tasks];
    updatedTasks[taskIndex] = {
      ...updatedTasks[taskIndex],
      [fieldToUpdate]: newValue,
    };

    setTasks(updatedTasks);
    updateLocalStorage('tasks', updatedTasks);
  };

  return (
    <div className="App">
      <OptionsPanel changeCard={changeCard} />
      <Card
        currentCard={currentCard}
        addTask={addTask}
        tasks={tasks}
        deleteTask={deleteTask}
        updateTask={updateTask}
        changeCard={changeCard}
      />
    </div>
  );
}

export default App;
